// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/IERC721Enumerable.sol"; 

interface uSTDInterface {
  function getAllRewards(address staker) external view returns (uint256);
}

contract W is ERC20Burnable, Ownable {
    using SafeMath for uint256;

    address uSTDAddress = 0x9823502B9bb0858C36fCAc909A976B2F7E9B31da; // change to live 
    uSTDInterface ustContract = uSTDInterface(uSTDAddress);

    mapping(address => uint256) public PreviousClaimedRewards;

    constructor() ERC20("W tester", "W test") {
    }


    function getAllRewards(address _staker) public view returns (uint256) {
        uint totalWMC = ustContract.getAllRewards(_staker);
        return totalWMC;
    }

    function claimWMC() public {
        require(getAllRewards(msg.sender) > 0, "you have no outstanding rewards");

        uint256 totalRewards = getAllRewards(msg.sender);
        uint256 previousRewards = PreviousClaimedRewards[msg.sender]; 
        uint256 toClaimRewards = totalRewards - previousRewards; 
        PreviousClaimedRewards[msg.sender] = PreviousClaimedRewards[msg.sender] + toClaimRewards;   
        _mint(msg.sender, toClaimRewards);                    
    }
}
